import axios from "axios"
// import { DRAFT_STATE } from "immer/dist/internal"
import { CART_ADD_ITEM, CART_REMOVE_ITEM , CART_SAVE_SHIPPING_ADDRESS, CART_SAVE_PAYMENT_METHOD} from "../const/cartConstant"

export const addToCart = (id, qty) => async (dispatch, getState) => {
    const { data } = await axios.get(`/api/products/${id}`)

    dispatch({
      type: CART_ADD_ITEM,
      payload: {
        product: data._id,
        name: data.name,
        image: data.image,
        price: data.price,
        countInStock: data.countInStock,
        qty,
      },
    })

    localStorage.setItem('cartItems', JSON.stringify(getState().cart.cartItems))
}
///getstate is used to get any state ->ptoducts, carts, orders
export const removeToCart = (id) => async (dispatch, getState) => {
    const { data } = await axios.get(`/api/products/${id}`)

    dispatch({
      type: CART_REMOVE_ITEM,
        payload: id,
    })
    localStorage.setItem('cartItems', JSON.stringify(getState().cart.cartItems))
}

export const saveShippingAdress = (data) => async (dispatch) => {

  dispatch({
    type: CART_SAVE_SHIPPING_ADDRESS,
      payload: data,
  })
  localStorage.setItem('shippingadress', JSON.stringify(data))
}

export const savePaymentMethod = (data) => async (dispatch) => {

  dispatch({
    type: CART_SAVE_PAYMENT_METHOD,
      payload: data,
  })
  localStorage.setItem('paymentMethod', JSON.stringify(data))
}

